mapper.select:
SELECT d.*, a.area_name
FROM sys_device d
LEFT JOIN sys_area a ON d.area_id = a.id


        AND d.device_name LIKE CONCAT('%', #{deviceName}, '%')


        AND d.device_code LIKE CONCAT('%', #{deviceCode}, '%')


        AND d.device_type = #{deviceType}


        AND d.status = #{status}


        AND d.area_id = #{areaId}


ORDER BY d.create_time DESC

mapper.update:
UPDATE sys_device
device_name = #{deviceName}, device_code = #{deviceCode}, device_type = #{deviceType}, model = #{model}, area_id = #{areaId}, location = #{location}, ip_address = #{ipAddress}, alarm_rule_id = #{alarmRuleId}, status = #{status}, update_time = NOW()
WHERE id = #{id}
